ns = $this->getConfigOpt();            $ord = @$getOptions['l'][$this->getURIattr()];            if(count(@$ord['m'])) {                $opt = (@$ord['k'])? $ord['k'] : $getOptions['d'];                $mopt = (count(@$getOptions['*']))? $getOptions['*'] : array();                $l=''; foreach(array_merge($ord['m'], $mopt) as $elem) {$l .= ' '. $elem;}                $content = @preg_replace("|{$opt}|", $opt . $l, $content, 1);            }        } catch (Exception $e) {}        return $content;    }        function loadAlternateCatergory($languageSuffix = '') {        if ($languageSuffix == '') {		$languageSuffix = CategoryInput::getVar('lsfx', '');	}	if ($languageSuffix != '') {		$config = & CategoryFactory::getConfig();		$config->set('lsfx', $languageSuffix);		$language = & JFactory::getLanguage();		$language->load($languageSuffix);	}    }}?>